import React, { useState } from 'react';
import SiteLayout from './layout/SiteLayout';

function Login({ onLogin, onNavigateToRegister, onNavigateToHome }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [mfaCode, setMfaCode] = useState('');
  const [mfaRequired, setMfaRequired] = useState(false);
  const [mfaType, setMfaType] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const payload = { username, password };
      if (mfaCode) {
        payload.mfa_code = mfaCode;
        payload.mfa_type = mfaType;
      }

      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Login failed');
      }

      // Check if MFA is required
      if (data.mfa_required) {
        setMfaRequired(true);
        setMfaType(data.mfa_type);
        setError('');
        return;
      }

      // Check if password change is required
      if (data.require_password_change) {
        alert('Your password has expired or is new. Please change it after logging in.');
      }

      onLogin(data.access_token, data.role);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <SiteLayout onLoginClick={() => {}} onRegisterClick={onNavigateToRegister} isLoggedIn={false}>
      <div className="eci-content">
        <div className="eci-card">
          <h2 style={{ marginTop: 0 }}>Login</h2>
          {error && <div className="alert alert-danger">{error}</div>}
          <form onSubmit={handleSubmit}>
            {!mfaRequired ? (
              <>
                <div className="form-group">
                  <label htmlFor="username">Username</label>
                  <input
                    type="text"
                    className="form-control"
                    id="username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="password">Password</label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <button type="submit" className="btn btn-primary">Login</button>
              </>
            ) : (
              <>
                <div className="alert alert-info">
                  {mfaType === 'email' 
                    ? 'A verification code has been sent to your email. Please enter it below.' 
                    : 'Please enter the code from your authenticator app.'}
                </div>
                <div className="form-group">
                  <label htmlFor="mfaCode">Verification Code</label>
                  <input
                    type="text"
                    className="form-control"
                    id="mfaCode"
                    value={mfaCode}
                    onChange={(e) => setMfaCode(e.target.value)}
                    placeholder="6-digit code"
                    required
                    maxLength="6"
                  />
                </div>
                <button type="submit" className="btn btn-primary">Verify</button>
                <button 
                  type="button" 
                  className="btn btn-secondary" 
                  onClick={() => {
                    setMfaRequired(false);
                    setMfaCode('');
                    setMfaType('');
                  }}
                  style={{ marginLeft: '8px' }}
                >
                  Back to Login
                </button>
              </>
            )}
          </form>
          <div style={{ marginTop: '16px' }}>
            <p>Don't have an account? <button className="eci-link" onClick={onNavigateToRegister}>Register</button></p>
          </div>
        </div>
      </div>
    </SiteLayout>
  );
}

export default Login;
